        val datablock = new CipherDataBlock(ciphertext.toCharArray(), AsciiAlphabet)

        val lengths = (2 to 10).toList
        val progress = new ProgressBar(lengths.size)
        val results = lengths.flatMap(length => {
            val permutations = lengths.permutations.toList
            val copiedText = datablock.clone().padToMultiple(length, 'X')
            println(s"${copiedText.size} ${length}")
            val decryptions = permutations.map(perm => TranspositionCipher.decrypt(copiedText, perm.toIndexedSeq))
            val filtered = decryptions.filter(x => !x.mkString.contains("\\|") && !x.mkString.contains("|/"))
            progress.increment()
            filtered
        });
        println(results.size)
    }

